二叉树的前中后序递归和非递归遍历和层序遍历代码实现

<!--more-->

```
#include<iostream>
#include<stdlib.h>
using namespace std;
#define STACK_INIT_SIZE 100//存储空间初始分配量
#define STACKINCREMENT 10//存储空间分配增量

typedef struct BiTNode {
	char data;
	struct BiTNode* lchild, * rchild;
}BiTNode,* BiTree;
typedef struct Node
{
	BiTNode* treeNode;
	Node* next;
}Node;
typedef struct LinkQueue {
	Node* front;
	Node* rear;

}LinkQueue;
typedef struct {
	BiTNode* top;
	BiTNode* base;
	int size;

}stack;
void createBiTree(BiTree& t);
void preOrderTraverse(BiTree t);
void inOrderTraverse1(BiTree t);
void postOrderTraverse(BiTree t);
int getDeep(BiTree t);
bool initQueue(LinkQueue& Q);
bool enQueue(LinkQueue& Q, BiTNode treeNode);
void deQueue(LinkQueue& Q);
void floorTraverse(LinkQueue& Q, BiTree& t);
void inOrderTraverse2(stack s, BiTree& t);
void initStack(stack& s);
bool isEmpty(stack& s);
void push(stack& s, BiTNode treeNode);
BiTNode pop(stack& s);

int main() {
	BiTree t;
	stack s;
	initStack(s);
	LinkQueue Q;
	initQueue(Q);
	createBiTree(t);
	cout << "二叉树创建完成" << endl<<endl;
	cout << "先序遍历" << endl;
	preOrderTraverse(t);
	cout << endl;
	cout << "递归中序遍历" <<endl;
	inOrderTraverse1(t);
	cout << endl;
	cout << "非递归中序遍历" << endl;
	inOrderTraverse2(s, t);
	cout << endl;
	cout << "后序遍历" << endl;
	postOrderTraverse(t);
	cout << endl;
	cout << "层序遍历" << endl;
	floorTraverse(Q, t);
	cout << endl;

	cout << "二叉树的深度为" << getDeep(t) << endl;
	return 0;
}
void createBiTree(BiTree& t) {
	char c;
	cin >> c;
	if (c == '$')
		//利用给先序遍历序列添加空节点保证建立二叉树的唯一性
		t = NULL;
	else {
		if (!(t = (BiTNode*)malloc(sizeof(BiTNode)))) {
			exit(OVERFLOW);
		}
		//申请该节点的内存空间
		//按照先序序列创建二叉树
		t->data = c;
		createBiTree(t->lchild);
		createBiTree(t->rchild);

​	}
​	return;
}
//先序遍历
void preOrderTraverse(BiTree t) {
​	if (t) {
​		cout << t->data << " " << flush;
​		preOrderTraverse(t->lchild);
​		preOrderTraverse(t->rchild);
​	}
}
//中序遍历
void inOrderTraverse1(BiTree t) {
​	if (t) {
​		inOrderTraverse1(t->lchild);
​		cout << t->data << " " << flush;
​		inOrderTraverse1(t->rchild);
​	}
}
//后序遍历
void postOrderTraverse(BiTree t) {
​	if (t) {
​		postOrderTraverse(t->lchild);
​		postOrderTraverse(t->rchild);
​		cout << t->data << " " << flush;
​	}
}
int getDeep(BiTree t) {
​	if (t == NULL) {
​		return 0;
​	}
​	//变成比较两个子树的深度问题,递归分解问题,最下面的时候返回1
​	int a = getDeep(t->lchild);
​	int b = getDeep(t->rchild);
​	return (a > b) ? (a + 1) : (b + 1);
}
bool initQueue(LinkQueue& Q) {
​	Q.front = Q.rear = new Node;
​	//这个链队列是具有头结点的链队列,头结点不存数据
​	if (!Q.front) {
​		exit(OVERFLOW);
​	}
​	Q.front->next = NULL;
​	return true;
}
bool enQueue(LinkQueue& Q, BiTNode *treeNode) {
​	Node* p = (Node*)malloc(sizeof(Node));
​	//如果内存申请失败
​	if (!p) {
​		exit(OVERFLOW);
​	}
​	//让p节点中的treeNode指向对应的树节点
​	p->treeNode =treeNode;
​	p->next = NULL;

​	//连接链表
​	Q.rear->next = p;

​	//头结点的data域为空,尾结点data域非空
​	Q.rear = p;
​	return true;

}
void deQueue(LinkQueue& Q) {
	if (Q.front && Q.front != Q.rear) {
		//新建一个temp指针p
		Node* p = Q.front->next;

​		Q.front->next = p->next;
​		//判断出队的是不是最后一个节点
​		//如果不加这个判断free(p后),Q.rear->next会变为野指针
​		if (p == Q.rear) {
​			Q.rear = Q.front;
​		}
​		free(p);
​	
​	}
​	return;
}
void floorTraverse(LinkQueue& Q, BiTree& t) {

​	if (t != NULL) {
​		enQueue(Q, t);
​	}
​	while (Q.front != Q.rear) {
​		cout << Q.front->next->treeNode->data <<' ' << flush;
​		if (Q.front->next->treeNode->lchild != NULL) {
​			enQueue(Q, Q.front->next->treeNode->lchild);
​		}
​		if (Q.front->next->treeNode->rchild != NULL) {
​			enQueue(Q, Q.front->next->treeNode->rchild);
​		}
​		deQueue(Q);
​	}
}

void inOrderTraverse2(stack s,BiTree& t) {

​	BiTNode* p = t;
​	while(p || !isEmpty(s)) {
​		if (p) {
​			push(s, *p);
​			p = p->lchild;
​		}
​		else {
​			BiTNode e = pop(s);
​			cout << e.data << " " << flush;
​			p = e.rchild;
​		}
​	}
​	return;
}
//栈相关函数
void initStack(stack& s) {
​	//申请内存空间
​	s.base = (BiTNode*)malloc(STACK_INIT_SIZE * sizeof(BiTNode));
​	if (!s.base) {
​		exit(OVERFLOW);
​	}
​	s.top = s.base;
​	s.size = STACK_INIT_SIZE;
​	return;
}
bool isEmpty(stack& s) {
​	if (s.base == s.top)
​		return true;
​	else
​		return false;
}
void push(stack& s, BiTNode treeNode) {
​	if (s.top - s.base >= s.size) {
​		s.base = (BiTNode*)realloc(s.base, (s.size + STACKINCREMENT) * sizeof(BiTNode));
​		if (!s.base) {
​			exit(OVERFLOW);
​		}
​		s.top = s.base + s.size;
​		s.size += STACKINCREMENT;
​	}
​	*s.top++ = treeNode;
}
BiTNode pop(stack& s) {
​	if (s.base) {	
​		BiTNode e = (*--s.top);
​		(*s.top).data = '$';
​		return e;
​	}
}


```

